<?phpclass Films_model extends Model {    public function __construct()    {        parent::__construct();    }	    //notre fonction    public function getFilms()    {		$lesFilms=$this->db->get('films');    	return $lesFilms->result();    }		function add($data) 	{		$this->db->insert('films', $data);		return;	}		function edit($idFilm, $titre, $title )	{		$data = array(			'titre' => $titre,			'title' => $title,			'url' => $url ,			'annee' => $annee ,			'image' => $image ,			'acteur' => $acteur ,			'realisateur' => $realisateur ,			'duree' => $duree ,			'genre' => $genre ,			'nationalite' => $nationalite ,			'synopsis' => $synopsis ,			'vu' => $vu ,			'etat' => $etat ,			'commentaire' => $commentaire ,			'avis' => $avis ,			'quand' => $quand		);				// 'type' => $type ,					$this->db->where('id', $idFilm);		$this->db->update('films', $data);	}		function delete($idFilm)	{		$this->db->where('id', $idFilm);		$this->db->delete('films');	}		public function getFilm($idFilm)    {		/* to debug SQL		$this->db->_compile_select();		*/		//$leFilm=$this->db->get_where('films', array('id' => $idFilm));				//$this->db->where('id', $this->uri->segment(4));				$this->db->where('id', $idFilm);		$leFilm=$this->db->get('films');		/* to debug SQL		echo $this->db->last_query();		*/    	return $leFilm->result();    }		public function getPrevious($idFilm)    {		$this->db->where('id <',$idFilm);		$this->db->order_by('id', 'desc');		$leFilm=$this->db->get('films', 1, 0);		    	return $leFilm->row();    }		public function getNext($idFilm)	{		$this->db->where('id >',$idFilm);		$this->db->order_by('id', 'asc');		$leFilm=$this->db->get('films', 1, 0);    	return $leFilm->row();    }			public function getMin()	{		$this->db->select_min('id');		$query = $this->db->get('films');		$min = $query->row(); 		return $min->id;	}		public function getMax()	{		$this->db->select_max('id');		$query = $this->db->get('films');		$max = $query->row(); 		return $max->id;	}}?>